INSERT INTO products(id, category_id, name, approximate_price, delivery_price, description, info)
VALUES
    ('3b280737-b12d-4b37-97b5-d1aa8b28b4b7', 'fd0dc401-559e-40e7-b676-4de659205b0a', 'The North Face QUEST - Kurtka przeciwdeszczowa', 399.0, 15.0, 'A versatile outdoor garment designed for hiking, trekking, and alpinism. Made from Polartec fabric, it offers high breathability while remaining fully windproof and waterproof, ensuring reliable protection and comfort in changing mountain conditions.', NULL),
    ('c0fe18c5-a845-44f9-b0a7-fbf2bf371b10', '490f6bde-a5cc-42fa-867a-85e5ec26bfc6', 'Bosch WAN2415GPL Washing Machine', 403.91, 15.0, 'The Bosch Serie 6 WGG154ZSPL washing machine offers a 10 kg load capacity, 1400 RPM spin speed, and 14 versatile programs. Its 70 L drum ensures perfectly clean laundry with minimal water usage, even for smaller loads, thanks to the ActiveWater Plus system. Energy efficient and quiet, it features an advanced side panel design that reduces vibrations and noise during spinning. Powered by the EcoSilence Drive motor, it also comes with the option of a free 10 year extended warranty when registered online.', NULL),
    ('aef25402-f076-4e8e-909c-250a2f6e6285', '490f6bde-a5cc-42fa-867a-85e5ec26bfc6', 'Bosch Series 6 WGG154ZSPL Washing Machine - 10 kg, 1400 RPM, steam, energy-efficient', 645.76, 15.0, 'The Bosch Serie 6 WGG154ZSPL washing machine offers a 10 kg load capacity, 1400 RPM spin speed, and 14 wash programs. The large 70 L VarioDrum ensures thorough cleaning even with smaller loads, supported by the water-saving ActiveWater Plus system. It operates in top energy class A and features a side-panel design that reduces noise and vibration during spinning. The EcoSilence Drive motor provides quiet, reliable performance and can be registered online for a free 10-year warranty. The Iron Assist steam function helps reduce wrinkles by up to 50 percent, making clothes easier to iron.', NULL),
    ('c8c87c85-af8e-4434-89f9-5a96fa25aa1e', 'fd0dc401-559e-40e7-b676-4de659205b0a', 'U.S. Polo Assn. - Bluza', 175.0, 15.0, 'A casual mens sweatshirt from U.S. Polo Assn., combining a clean design with everyday comfort. Made from soft, durable fabric, it offers a relaxed fit suitable for regular wear. Ideal for layering and versatile styling.', NULL),
    ('5dc21605-a868-4630-9aa4-55b1617a4c28', '490f6bde-a5cc-42fa-867a-85e5ec26bfc6', 'Bosch WGB2440EPL Washing Machine - 1400 RPM, 9 kg, 69 dB, White', 1044.74, 15.0, 'The Bosch Serie 6 WGG154ZSPL washing machine offers a 10 kg load capacity, 1400 RPM spin speed, and 14 wash programs. The 70 L VarioDrum ensures efficient and gentle washing, while ActiveWater Plus optimizes water usage even for small loads. The machine operates in energy class A and features a side-panel design that reduces noise and vibration. The EcoSilence Drive motor provides quiet, durable performance and can be registered online for a free 10-year warranty. The Iron Assist steam program reduces wrinkles by up to 50 percent, making ironing easier and refreshing shirts or garments in about 20 minutes.', NULL),
    ('64332f12-fbfd-4502-ae5b-2b3d1a6e7f94', '490f6bde-a5cc-42fa-867a-85e5ec26bfc6', 'Whirlpool Industrial Washing Machine AWG1114SD', 1139.05, 15.0, 'The WHIRLPOOL AWG1114SD is an industrial front-loading washing machine designed for heavy-duty use. It offers an 11 kg load capacity with 14 versatile programs to handle a wide range of laundry needs. The machine features a clear display and electro-mechanical controls for easy operation. Built for efficiency, it holds an energy rating of Class A and operates quietly, with a noise level of 76–80 dB during spinning. Its robust design, durable materials, and reliable performance make it ideal for commercial or industrial laundry environments.', NULL),
    ('2a8a260f-e99e-44f8-a455-6006a794c2ba', '490f6bde-a5cc-42fa-867a-85e5ec26bfc6', 'Whirlpool FFB9258SVPL Washing Machine – 9 kg, 1200 RPM, 14 programs, Inverter motor, FreshCare+', 335.42, 15.0, 'The Whirlpool FFB9258SVPL is a freestanding front-loading washing machine designed for convenient and efficient home laundry. With a 9 kg drum and 1200 RPM spin speed, it handles everyday laundry with ease. The machine offers 14 versatile programs to suit different fabric types and soil levels. Key features include FreshCare+, which keeps clothes fresh for hours after the cycle ends by gently massaging fabrics with steam and drum movements, and Steam Refresh, which removes odors and smooths wrinkles in just 20 minutes. The Sense Inverter motor ensures quiet, energy-efficient, and long-lasting performance. Additional features include foam and imbalance control, adjustable spin speed, end-of-cycle indicator, and a child lock for safety. This washing machine combines advanced technology with practical convenience for everyday use.', NULL);
